(function(e, t) {
  function n(e) {
    t.ajax({
      type: "POST",
      url: "/cart/update.js",
      data: "attributes[dev]=" + (e ? "true" : "false"),
      dataType: "json",
      success: function() {
        console.log("DevMode " + (e ? "enabled" : "disabled") + ". F5 again");
      }
    });
  }
  e.enterDevMode = function() {
    n(true);
  };
  e.exitDevMode = function() {
    n(false);
  };
})(window, jQuery);
